use bitwarden_crypto::{HashPurpose, MasterKey};

use crate::{
    auth::determine_password_hash,
    client::{LoginMethod, UserLoginMethod},
    error::{Error, Result},
    Client,
};

/// Validate if the provided password matches the password hash stored in the client.
pub(crate) fn validate_password(
    client: &Client,
    password: String,
    password_hash: String,
) -> Result<bool> {
    let login_method = client
        .internal
        .get_login_method()
        .ok_or(Error::NotAuthenticated)?;

    #[allow(irrefutable_let_patterns)]
    if let LoginMethod::User(login_method) = login_method.as_ref() {
        match login_method {
            UserLoginMethod::Username { email, kdf, .. }
            | UserLoginMethod::ApiKey { email, kdf, .. } => {
                let hash = determine_password_hash(
                    email,
                    kdf,
                    &password,
                    HashPurpose::LocalAuthorization,
                )?;

                Ok(hash == password_hash)
            }
        }
    } else {
        Err(Error::NotAuthenticated)
    }
}

#[cfg(feature = "internal")]
pub(crate) fn validate_password_user_key(
    client: &Client,
    password: String,
    encrypted_user_key: String,
) -> Result<String> {
    use crate::key_management::SymmetricKeyRef;

    let login_method = client
        .internal
        .get_login_method()
        .ok_or(Error::NotAuthenticated)?;

    #[allow(irrefutable_let_patterns)]
    if let LoginMethod::User(login_method) = login_method.as_ref() {
        match login_method {
            UserLoginMethod::Username { email, kdf, .. }
            | UserLoginMethod::ApiKey { email, kdf, .. } => {
                let master_key = MasterKey::derive(&password, email, kdf)?;
                let user_key = master_key
                    .decrypt_user_key(encrypted_user_key.parse()?)
                    .map_err(|_| "wrong password")?;

                let crypto_service = client.internal.get_crypto_service();
                let ctx = crypto_service.context();
                #[allow(deprecated)]
                let existing_key = ctx.dangerous_get_symmetric_key(SymmetricKeyRef::User)?;

                if user_key.to_vec() != existing_key.to_vec() {
                    return Err("wrong user key".into());
                }

                Ok(master_key
                    .derive_master_key_hash(password.as_bytes(), HashPurpose::LocalAuthorization)?)
            }
        }
    } else {
        Err(Error::NotAuthenticated)
    }
}

#[cfg(test)]
mod tests {
    use bitwarden_crypto::Kdf;

    use crate::auth::password::{validate::validate_password_user_key, validate_password};

    #[test]
    fn test_validate_password() {
        use std::num::NonZeroU32;

        use crate::client::{Client, LoginMethod, UserLoginMethod};

        let client = Client::new(None);
        client
            .internal
            .set_login_method(LoginMethod::User(UserLoginMethod::Username {
                email: "test@bitwarden.com".to_string(),
                kdf: Kdf::PBKDF2 {
                    iterations: NonZeroU32::new(100_000).unwrap(),
                },
                client_id: "1".to_string(),
            }));

        let password = "password123".to_string();
        let password_hash = "7kTqkF1pY/3JeOu73N9kR99fDDe9O1JOZaVc7KH3lsU=".to_string();

        let result = validate_password(&client, password, password_hash);

        assert!(result.unwrap());
    }

    #[cfg(feature = "internal")]
    #[test]
    fn test_validate_password_user_key() {
        use std::num::NonZeroU32;

        use bitwarden_crypto::{Kdf, MasterKey};

        use crate::client::{Client, LoginMethod, UserLoginMethod};

        let client = Client::new(None);

        let password = "asdfasdfasdf";
        let email = "test@bitwarden.com";
        let kdf = Kdf::PBKDF2 {
            iterations: NonZeroU32::new(600_000).unwrap(),
        };

        client
            .internal
            .set_login_method(LoginMethod::User(UserLoginMethod::Username {
                email: email.to_string(),
                kdf: kdf.clone(),
                client_id: "1".to_string(),
            }));

        let master_key = MasterKey::derive(password, email, &kdf).unwrap();

        let user_key = "2.Q/2PhzcC7GdeiMHhWguYAQ==|GpqzVdr0go0ug5cZh1n+uixeBC3oC90CIe0hd/HWA/pTRDZ8ane4fmsEIcuc8eMKUt55Y2q/fbNzsYu41YTZzzsJUSeqVjT8/iTQtgnNdpo=|dwI+uyvZ1h/iZ03VQ+/wrGEFYVewBUUl/syYgjsNMbE=";
        let private_key = "2.yN7l00BOlUE0Sb0M//Q53w==||09J28iXv9oWzYtzK2LBT6Yht4IT4MijEkk0fwFdrVQ4=".parse().unwrap();

        client
            .internal
            .initialize_user_crypto_master_key(master_key, user_key.parse().unwrap(), private_key)
            .unwrap();

        let result =
            validate_password_user_key(&client, "asdfasdfasdf".to_owned(), user_key.to_string())
                .unwrap();

        assert_eq!(result, "aOvkBXFhSdgrBWR3hZCMRoML9+h5yRblU3lFphCdkeA=");
        assert!(validate_password(&client, password.to_owned(), result).unwrap())
    }

    #[cfg(feature = "internal")]
    #[test]
    fn test_validate_password_user_key_wrong_password() {
        use std::num::NonZeroU32;

        use bitwarden_crypto::{Kdf, MasterKey};

        use crate::client::{Client, LoginMethod, UserLoginMethod};

        let client = Client::new(None);

        let password = "asdfasdfasdf";
        let email = "test@bitwarden.com";
        let kdf = Kdf::PBKDF2 {
            iterations: NonZeroU32::new(600_000).unwrap(),
        };

        client
            .internal
            .set_login_method(LoginMethod::User(UserLoginMethod::Username {
                email: email.to_string(),
                kdf: kdf.clone(),
                client_id: "1".to_string(),
            }));

        let master_key = MasterKey::derive(password, email, &kdf).unwrap();

        let user_key = "2.Q/2PhzcC7GdeiMHhWguYAQ==|GpqzVdr0go0ug5cZh1n+uixeBC3oC90CIe0hd/HWA/pTRDZ8ane4fmsEIcuc8eMKUt55Y2q/fbNzsYu41YTZzzsJUSeqVjT8/iTQtgnNdpo=|dwI+uyvZ1h/iZ03VQ+/wrGEFYVewBUUl/syYgjsNMbE=";
        let private_key = "2.yN7l00BOlUE0Sb0M//Q53w==||09J28iXv9oWzYtzK2LBT6Yht4IT4MijEkk0fwFdrVQ4=".parse().unwrap();

        client
            .internal
            .initialize_user_crypto_master_key(master_key, user_key.parse().unwrap(), private_key)
            .unwrap();

        let result =
            validate_password_user_key(&client, "asdfasdfasdf".to_string(), user_key.to_string())
                .unwrap();

        assert_eq!(result, "aOvkBXFhSdgrBWR3hZCMRoML9+h5yRblU3lFphCdkeA=");
        assert!(validate_password(&client, "asdfasdfasdf".to_string(), result.to_string()).unwrap())
    }
}
